<script>
	function scrollToSection(sectionId, duration) {
		var section = document.getElementById(sectionId);
		var start = window.scrollY;
		var target = section.getBoundingClientRect().top;
		var startTime;

		function scrollAnimation(currentTime) {
			if (!startTime) startTime = currentTime;
			var progress = currentTime - startTime;
			window.scrollTo(0, easeInOutQuad(progress, start, target, duration));

			if (progress < duration) {
				requestAnimationFrame(scrollAnimation);
			}
		}

		function easeInOutQuad(t, b, c, d) {
			t /= d / 2;
			if (t < 1) return c / 2 * t * t + b;
			t--;
			return -c / 2 * (t * (t - 2) - 1) + b;
		}

		requestAnimationFrame(scrollAnimation);
	}


	function displayFileName(input) {
		const fileName = input.files[0].name;
		document.getElementById('file-name').innerText = `Selected File: ${fileName}`;
	}
</script>

