	$("#submit-btn").on("click", function() {
		var form = $('#register-form')[0];
		var warnArray = [];
		for (var i = 0; i < form.elements.length; i++) {
			if (form.elements[i].value === '' && form.elements[i].hasAttribute('required')) { console.log($(form.elements[i]).siblings("label").text());
				var cont = '<strong style="text-transform: capitalize;">' + $(form.elements[i]).siblings(".main-label").text() + '</strong> is required<br><br>';
				warnArray.push(cont);
			}
		}
		if ($('input[name="presentation"]:checked').length < 1) {
			var cont = '<strong style="text-transform: capitalize;">Type of Presentation</strong> is required<br><br>';
			warnArray.push(cont);
		}
		if ($('input[name="conference[]"]:checked').length < 1) {
			var cont = '<strong style="text-transform: capitalize;">Conference Topic</strong> is required<br><br>';
			warnArray.push(cont);
		}
		if (cont) {
			$.dialog({
				type: 'orange',
				title: 'Warning',
				content: warnArray,
				boxWidth: '30%',
				useBootstrap: false,
			});
			return false;
		}
	});

	$("#register-form").submit(function(e) {
		e.preventDefault();
		var form = $(this);
		var formData = new FormData($(this)[0]);
		var actionUrl = form.attr('action');
		var mail = $('#email').val();
		var rmail = $('#r-email').val();
		if (rmail != mail) {
			$.dialog({
				type: 'red',
				title: 'Failed',
				content: 'Email is not matching',
				boxWidth: '30%',
				useBootstrap: false,
			});
			return false;
		}

		$.ajax({
			url: actionUrl,
			type: "POST",
			dataType: "json",
			data: formData,
			processData: false,
			contentType: false,
			async: false,
			success: function(data) {
				if (!data['error']) {
					$.dialog({
						type: 'green',
						title: 'Success',
						content: data['message'],
						boxWidth: '30%',
						useBootstrap: false,
					});
					$('#register-form')[0].reset();
					$('.file-name').text('');
				} else {
					$.dialog({
						type: 'red',
						title: 'Failed',
						content: data['message'],
						boxWidth: '30%',
						useBootstrap: false,
					});
				}
			},
			error: function(error) {
				$.dialog({
					type: 'red',
					title: 'Failed',
					content: 'Something went wrong',
					boxWidth: '30%',
					useBootstrap: false,
				});
			}
		});
	});
</script>

